import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import API from '../axios/Api';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import '../styles/index.css';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'; 

function DataTable({ mhs, refresh }) {
  const [showEditModal, setShowEditModal] = useState(false);
  const [editFormData, setEditFormData] = useState({
    num_docto: mhs.num_docto,
    fornecedor: mhs.fornecedor,
    dt_lancamento: mhs.dt_lancamento,
    dt_vencimento: mhs.dt_vencimento,
    valor: mhs.valor,
    observacao: mhs.observacao
  });

  const deleteMhs = async () => {
    await API.delete('deletemhs.php?id=' + mhs.id);
    return refresh();
  };

  const deleteConfirm = () => {
    confirmAlert({
      title: 'Confirme a ação',
      message: 'Você deseja excluir o ID: ' + mhs.num_docto + ' ?',
      buttons: [
        {
          className: 'confirm-delete',
          label: 'Deletar',
          onClick: () => deleteMhs()
        },
        {
          label: 'Não, voltar',
          onClick: () => {}
        }
      ]
    });
  };

  const handleEditModalShow = () => {
    setShowEditModal(true);
  };

  const handleEditModalClose = () => {
    setShowEditModal(false);
  };

  const handleEditFormChange = (e) => {
    setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
  };

  const handleEditFormSubmit = async (event) => {
    event.preventDefault();
    // Faça a lógica para enviar as alterações para o servidor ou atualizar o estado global

    // Exemplo de chamada API de atualização:
    // try {
    //   const response = await API.put(`/updatemhs.php?id=${mhs.id}`, editFormData, {
    //     headers: {
    //       'Content-Type': 'application/json'
    //     }
    //   });
    //   console.log(response.data); // Exibe a resposta do servidor no console

    //   handleClose();
    //   window.location.reload();
    // } catch (error) {
    //   console.log(error);
    // }
  };

  return (
    <tr className="status">
      <td style={{ backgroundColor: mhs.situacao === 'Aberto' ? 'green' : mhs.situacao === 'Vencida' ? 'orange' : mhs.situacao === 'Recebida' ? 'red' : '' }}>
        {mhs.num_docto} {mhs.situacao}
      </td>
      <td>{mhs.fornecedor}</td>
      <td>{mhs.dt_lancamento}</td>
      <td>{mhs.dt_vencimento}</td>
      <td>{mhs.valor}</td>
      <td>{mhs.observacao}</td>
      <td className="action-icons">
        <Link className="edit">
          <i className="fa fa-pencil-square-o" id="Edit-icon" onClick={handleEditModalShow}></i>
        </Link>

        <Link className="delete">
          <i className="fa fa-trash" id="Delete-icon" onClick={deleteConfirm}></i>
        </Link>
      </td>

      <Modal show={showEditModal} onHide={handleEditModalClose}>
        <Modal.Header>
          <Modal.Title>Editar Cadastro</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleEditFormSubmit}>
            <Form.Group className="mb-3" controlId="num_docto">
              <Form.Label>Numero do Documento *</Form.Label>
              <Form.Control
                type="text"
                name="num_docto"
                value={editFormData.num_docto}
                onChange={handleEditFormChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="fornecedor">
              <Form.Label>Fornecedor *</Form.Label>
              <Form.Control
                type="text"
                name="fornecedor"
                value={editFormData.fornecedor}
                onChange={handleEditFormChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="dt_lancamento">
              <Form.Label>Data de Emissão *</Form.Label>
              <Form.Control
                type="text"
                name="dt_lancamento"
                value={editFormData.dt_lancamento}
                onChange={handleEditFormChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="dt_vencimento">
              <Form.Label>Data de Vencimento *</Form.Label>
              <Form.Control
                type="text"
                name="dt_vencimento"
                value={editFormData.dt_vencimento}
                onChange={handleEditFormChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="valor">
              <Form.Label>Valor *</Form.Label>
              <Form.Control
                type="text"
                name="valor"
                value={editFormData.valor}
                onChange={handleEditFormChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="observacao">
              <Form.Label>Observação *</Form.Label>
              <Form.Control
                type="text"
                name="observacao"
                value={editFormData.observacao}
                onChange={handleEditFormChange}
              />
            </Form.Group>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleEditModalClose}>
                Fechar
              </Button>
              <Button variant="primary" type="submit">
                Salvar Alterações
              </Button>
            </Modal.Footer>
          </Form>
        </Modal.Body>
      </Modal>
    </tr>
  );
}

export default DataTable;
