import React from 'react';
import { Link } from 'react-router-dom';
import API from '../axios/Api';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import { confirmAlert } from 'react-confirm-alert';
import 'react-confirm-alert/src/react-confirm-alert.css';
import '../styles/index.css';
import EditModal from './EditModal';

function DataTable({ mhs, refresh }) {
  async function deleteMhs() {
    await API.delete('deletemhs.php?id=' + mhs.id)
    return refresh();
  }

  function deleteConfirm() {
    confirmAlert({
      title: 'Confirme a ação',
      message: "Você deseja excluir o ID: " + mhs.num_docto + " ? ",
      buttons: [
        {
          className: 'confirm-delete',
          label: 'Deletar',
          onClick: () => deleteMhs()
        },
        {
          label: 'Não, voltar',
          onClick: () => { }
        },
      ]
    })
  }

  return (
    <tr className='status'>
      <td style={{ backgroundColor: mhs.situacao === 'Aberto' ? 'green' : mhs.situacao === 'Vencida' ? 'orange' : mhs.situacao === 'Recebida' ? 'red' : '' }}>
        {mhs.num_docto} {mhs.situacao}
      </td>
      <td>{mhs.fornecedor}</td>
      <td>{mhs.dt_lancamento}</td>
      <td>{mhs.dt_vencimento}</td>
      <td>{mhs.valor}</td>
      <td>{mhs.observacao}</td>
      <td className='action-icons'>
        <Link to={"/edit/" + mhs.id} className="edit">
          <i className="fa fa-pencil-square-o" id="Edit-icon"></i>
        </Link>

        <Link className="delete">
          <i className="fa fa-trash" id="Delete-icon" onClick={deleteConfirm}></i>
        </Link>
      </td>
    </tr>
  );
}

export default DataTable;
