import React, { Component } from 'react';
import API from '../axios/Api';
import DataTable from './DataTable';

export class DaftarMhs extends Component {
  state = {
    mhs: [],
    order: 'asc' // Ordem inicial: ascendente
  };

  componentDidMount = async () => {
    await this.fetchData();
  };

  fetchData = async () => {
    const { order } = this.state;
    await API.get(`/viewdata.php?order=${order}`)
      .then((response) =>
        this.setState({
          mhs: response.data,
        })
      );
  };

  handleOrderChange = async (e) => {
    const order = e.target.value;
    await this.setState({ order });
    await this.fetchData();
  };

  render() {
    const { mhs, order } = this.state;

    const renderData = mhs.map((mhs) => {
      return <DataTable mhs={mhs} key={mhs.id} refresh={this.fetchData} />;
    });

    return (
      <div className="container" style={{ marginTop: '20px' }}>
        <div className="row">
          <div className="col-lg-12">
            <div>
              <label htmlFor="orderSelect">Ordenar por:</label>
              <select id="orderSelect" value={order} onChange={this.handleOrderChange}>
                <option value="asc">Crescente</option>
                <option value="desc">Decrescente</option>
              </select>
            </div>
            <table className="table table-bordered table-striped table-hover">
              <thead>
                <tr>
                  <th>Nº Docto</th>
                  <th>Fornecedor</th>
                  <th>Lançamento</th>
                  <th>Vencimento</th>
                  <th>Valor R$</th>
                  <th>Pagamento</th>
                </tr>
              </thead>
              <tbody>{renderData}</tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default DaftarMhs;
